import axios from 'axios';
import Comment from './comment.vue';
export default {
  data: function data() {
    return {
      username: '',
      password: '',
      yzm: '',
      text: '',
      list: []
    };
  },
  component: {
    Comment: Comment
  },
  methods: {
    faceC: function faceC() {
      $("#bbox").toggle();
    },
    fadeIn: function fadeIn() {
      $("#apply").toggle({
        display: "block"
      });
    },
    sayBlur: function sayBlur() {
      if ($("#say").val() !== "来说两句吧...") {
        $("#content").html("");
      }
    },
    //验证登录后可评论
    //https://codepen.io/xiguaxigua/pen/zoXWVX
    showSend: function showSend() {
      if (this.text) {
        this.$emit('send', this.text);
        this.text = '';
      } else {
        alert('评论不能为空');
      }

      if ($("#say").val() == "来说两句吧...") {
        $("#content").html("评论内容不能为空!"); // history.go(0);
      } else if ($("#login_name").html() == "") {
        alert("登录后才可以评论哦~");
      } else {
        var url = "http://127.0.0.1/myproject1_show/blog_user/blog/data/message.php?uname=" + this.username + "&text=" + this.text;
        axios.get(url).then(function (result) {
          console.log(result);
        }).catch(function (err) {
          console.log(err);
        });
        var urli = "http://127.0.0.1/myproject1_show/blog_user/blog/data/read_message.php";
        axios.get(urli).then(function (result) {
          console.log(result.data);
        }).catch(function (err) {
          console.log(err);
        });
      }
    },
    showLogin: function showLogin() {
      var html = document.querySelector("html");
      var body = document.querySelector("body");
      html.style.overflow = "hidden";
      html.style.height = "100%";
      body.style.overflow = "hidden";
      body.style.height = "100%";
      $("#hidden").css({
        display: "block"
      });
    },
    closeLogin: function closeLogin() {
      $("#hidden").css({
        display: "none"
      });
      var html = document.querySelector("html");
      var body = document.querySelector("body");
      html.style.overflow = "visible";
      html.style.height = "100%";
      body.style.overflow = "visible";
      body.style.height = "100%";
    },
    unameLogin: function unameLogin() {
      if ($("#uname").val() == "") {
        $("#spn").html("用户名不能为空");
      } else {
        var $reg = /^[a-zA-Z0-9_]{3,12}$/;

        if (!$reg.test($("#uname").val())) {
          $("#spn").html("请输入3到12位用户名");
        } else {
          $("#spn").html("√");
        }
      }
    },
    upwdLogin: function upwdLogin() {
      if ($("#upwd").val() == "") {
        $("#spu").html("密码不能为空");
      } else {
        var $reg = /^[a-zA-Z0-9_]{6,12}$/;

        if (!$reg.test($("#upwd").val())) {
          $("#spu").html("请输入6到12位密码");
        } else {
          $("#spu").html("√");
        }
      }
    },
    yzmLogin: function yzmLogin() {
      if ($("#yzm").val() == "") {
        $("#yanz").html("验证码不能为空");
      } else {
        $("#yanz").html("√");
      }
    },
    submmitL: function submmitL() {
      var username = $("#uname").val();
      var password = $("#upwd").val();
      var yzm = $("#yzm").val();

      if ($("#uname").val() == "") {
        $("#spn").html("用户名不能为空");
        return false;
      } else {
        var $reg = /^[a-zA-Z0-9_]{3,12}$/;

        if (!$reg.test($("#uname").val())) {
          $("#spn").html("请输入3到12位用户名");
          return false;
        }
      }

      if ($("#upwd").val() == "") {
        $("#spu").html("密码不能为空");
        return false;
      } else {
        var $reg = /^[a-zA-Z0-9_]{6,12}$/;

        if (!$reg.test($("#upwd").val())) {
          $("#spu").html("请输入6到12位密码");
          return false;
        }
      }
    },
    doLogin: function doLogin() {
      //异步请求
      var url = "http://127.0.0.1/myproject1_show/blog_user/blog/data/login.php?username=".concat(this.username, "&password=").concat(this.password);
      axios.get(url).then(function (res) {
        console.log(res.data);

        if (res.data.code == 1) {
          alert(res.data.msg); //  this.$router.push("/");

          $("#isLogin>p").html("欢迎" + res.data.data[1]); //登录成功之后页面可以滚动

          $("#hidden").css({
            display: "none"
          });
          var html = document.querySelector("html");
          var body = document.querySelector("body");
          html.style.overflow = "visible";
          html.style.height = "100%";
          body.style.overflow = "visible";
          body.style.height = "100%";

          if (!res.data.data[0]) {
            alert("未登录");
          } else {
            //个人中心 点击登录出现个人中心页面
            $("text").click(function () {
              $("#enter-per").fadeIn();
              $("#hidden").css({
                display: "none"
              });
            });
            $("#close").click(function () {
              $("#enter-per").fadeOut();
              var html = document.querySelector("html");
              var body = document.querySelector("body");
              html.style.overflow = "visible";
              html.style.height = "100%";
              body.style.overflow = "visible";
              body.style.height = "100%";
            });
          }
        } else alert(res.data.msg);
      }).catch(function (err) {
        console.log(err);
      });
      $("#top2>li").click(function () {
        var li = $(this);
        var i = li.index();
        $("#top3>ul").eq(i).fadeIn().siblings().hide();
        $(this).css({
          borderBottom: "2px solid #1983FD"
        }).siblings().css({
          borderBottom: "none"
        });
      });
      $("#personal-l>ul>li:first-child>span").hover(function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/tab-list-icon-active1.png') + ")"
        });
        $(this).next().css({
          color: "#1983FD"
        });
      }, function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/tab-list-icon1.png') + ")"
        });
        $(this).next().css({
          color: "#666666"
        });
      }); //2

      $("#personal-l>ul>li:nth-child(2)>span").hover(function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/task-ico-active.png') + ")"
        });
        $(this).next().css({
          color: "#1983FD"
        });
      }, function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/task-ico.png') + ")"
        });
        $(this).next().css({
          color: "#666666"
        });
      }); //3

      $("#personal-l>ul>li:nth-child(3)>span").hover(function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/hots_ico_active.png') + ")"
        });
        $(this).next().css({
          color: "#1983FD"
        });
      }, function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/hots_ico.png') + ")"
        });
        $(this).next().css({
          color: "#666666"
        });
      }); //4

      $("#personal-l>ul>li:nth-child(4)>span").hover(function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/tab-list-icon-active-notice.png') + ")"
        });
        $(this).next().css({
          color: "#1983FD"
        });
      }, function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/tab-list-notice.png') + ")"
        });
        $(this).next().css({
          color: "#666666"
        });
      }); //5

      $("#personal-l>ul>li:nth-child(5)>span").hover(function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/tab-list-icon-active5.png') + ")"
        });
        $(this).next().css({
          color: "#1983FD"
        });
      }, function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/tab-list-icon5.png') + ")"
        });
        $(this).next().css({
          color: "#666666"
        });
      }); //6

      $("#personal-l>ul>li:nth-child(6)>span").hover(function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/set-icon-active.png') + ")"
        });
        $(this).next().css({
          color: "#1983FD"
        });
      }, function () {
        $(this).css({
          backgroundImage: "url(" + require('../assets/img/login/set-icon.png') + ")"
        });
        $(this).next().css({
          color: "#666666"
        });
      }); //点击修改图标

      $("#ph").hover(function () {
        $("#ph").hide();
      }, function () {
        $("#pic").show();
      });
      $("#pic").mouseout(function () {
        $("#ph").show();
        $("#pic").hide();
      }); //点击修改昵称

      $("#top1>em").click(function () {
        $("#user").hide();
        $("#top1>em").hide();
        $("#click-ins").fadeIn();
        $("#insert").val($("#user").html());
      });
      $("#send").click(function () {
        if ($("#insert").val() == "") {
          alert("昵称不能为空");
        } else {
          alert("昵称已经提交，请等待审核");
          $("#user").show();
          $("#top1>em").show();
          $("#click-ins").hide();
        }
      }); //个人中心右侧

      $("#personal-l>ul>li").click(function () {
        var $li = $(this);
        var i = $li.index();
        $("#personal-change>div").eq(i).fadeIn().siblings().hide();
      });
      $("#personal-set>div:nth-child(2)>ul>li").click(function () {
        var $li = $(this);
        var i = $li.index();
        $("#personal-set>div:nth-child(2)>ul>li>i").eq(i).css({
          backgroundImage: "url(" + require('../assets/img/login/btn-click.png') + ")"
        });
      });
    },
    //退出登录  
    loginout: function loginout() {
      var url = 'http://127.0.0.1/myproject1_show/blog_user/blog/data/signout.php';
      axios.get(url).then(function (res) {
        console.log(res);
      }).catch(function (err) {
        console.log(err);
        alert("退出成功");
        location.reload(true);
      });
    }
  },
  computed: {},
  watch: {},
  mounted: function mounted() {
    //页面挂载出来，可以滚动
    $("#hidden").css({
      display: "none"
    });
    var html = document.querySelector("html");
    var body = document.querySelector("body");
    html.style.overflow = "visible";
    html.style.height = "100%";
    body.style.overflow = "visible";
    body.style.height = "100%";
  }
};
